<?php get_header(); ?>	<section id="primary" class="site-content">		<div id="content" role="main" class="border-page">		<?php if(function_exists('cmp_breadcrumbs')) cmp_breadcrumbs();?>		<?php if ( have_posts() ) : ?>			<?php the_post(); ?>			<header class="archive-header">				<h1 class="archive-title"><?php printf( __( 'Author Archives: %s', 'hmjblog' ), '<span class="vcard"><a class="url fn n" href="' . esc_url( get_author_posts_url( get_the_author_meta( 'ID' ) ) ) . '" title="' . esc_attr( get_the_author() ) . '" rel="me">' . get_the_author() . '</a></span>' ); ?></h1>			</header>			<?php rewind_posts(); ?>			<?php			if ( get_the_author_meta( 'description' ) ) : ?>			<div class="author-info">				<div class="author-avatar">					<?php					$author_bio_avatar_size = apply_filters( 'hmjblog_author_bio_avatar_size', 68 );					echo get_avatar( get_the_author_meta( 'user_email' ), $author_bio_avatar_size );					?>				</div>				<div class="author-description">					<h2><?php printf( __( '关于 %s', 'hmjblog' ), get_the_author() ); ?></h2>					<p><?php the_author_meta( 'description' ); ?></p>				</div>			</div>			<?php endif; ?>			<?php while ( have_posts() ) : the_post(); ?>				<?php get_template_part( 'template-all', get_post_format() ); ?>			<?php endwhile; ?>		<?php endif; ?>		<?php dmeng_paging_nav(); ?>		</div>	</section><?php get_sidebar(); ?><?php get_footer(); ?>