<?phpif ( post_password_required() )	return;?><div id="comments" class="comments-area">	<?php if ( have_comments() ) : ?>		<h2 class="comments-title">			<?php				printf( _n( '1 条评论', '%1$s 条评论', get_comments_number(), 'hmjblog' ),					number_format_i18n( get_comments_number() ), '<span>' . get_the_title() . '</span>' );			?>		</h2>		<ol class="commentlist">			<?php wp_list_comments( array( 'callback' => 'hmjblog_comment', 'style' => 'ol' ) ); ?>		</ol>		<?php if ( get_comment_pages_count() > 1 && get_option( 'page_comments' ) ) : ?>		<nav id="comments-navi">    			<?php paginate_comments_links('prev_text=«&next_text=»');?>		</nav>		<?php endif; ?>		<?php		if ( ! comments_open() && get_comments_number() ) : ?>		<p class="nocomments"><?php _e( '评论已经被关闭。' , 'hmjblog' ); ?></p>		<?php endif; ?>	<?php endif; ?>	<?php comment_form(); ?></div>