<?php get_header(); ?>	<section id="primary" class="site-content">		<div id="content" role="main" class="border-page">		<?php if(function_exists('cmp_breadcrumbs')) cmp_breadcrumbs();?>		<?php if ( have_posts() ) : ?>			<header class="page-header">				<h1 class="page-title"><?php printf( __( '搜索结果: %s', 'hmjblog' ), '<span>' . get_search_query() . '</span>' ); ?></h1>			</header>			<?php while ( have_posts() ) : the_post(); ?>				<?php get_template_part( 'template-all', get_post_format() ); ?>			<?php endwhile; ?>		<?php else : ?>			<article id="post-0" class="post no-results not-found">				<header class="entry-header">					<h1 class="entry-title"><?php _e( '没有搜索到相应结果！', 'hmjblog' ); ?></h1>				</header>				<div class="entry-content">					<p></p>					<p><?php _e( '　　很抱歉，没有找到符合您条件的搜索结果，请再次尝试输入一些不同的关键字进行搜索。', 'hmjblog' ); ?></p>				</div>			</article>		<?php endif; ?>		<?php dmeng_paging_nav(); ?>		</div>	</section><?php get_sidebar(); ?><?php get_footer(); ?>